package JavaLearing;

public class DataType {

	public static void main(String[] args) { 
		// TODO Auto-generated method stub
		
		int i=10;
		i=20;
	System.out.println(i); //20 will be output
	boolean f= true; //true is a value as well as keyword in java library
	System.out.println(f);
	char a='A';
	System.out.print(a);
	String b= "delete"; //string is a class and can be used as data type to hold string values
	System.out.println(b);
	double k=10.20;// can hold integer
	System.out.println(k);
	
	}

}
